const tasks = [
    {
        id: 1,
        text: "Doctors Appointment",
        day: 'Feb 5th at 9:10pm',
        reminder: true
    },
    {
        id: 2,
        text: " Shoping",
        day: 'Feb 7th at 1:30pm',
        reminder: false
    },
    {
        id: 3,
        text: "Visit my friends",
        day: 'Feb 10th at 8:00am',
        reminder: false
    }
];

const Tasks = () => {
    return (
        <div>
            {tasks.map((task) => (
               <h1 key={task.id}>{task.text}</h1>
               ))} 
        </div>
    )
}

export default Tasks
